trigger.body:
v_mensaje VARCHAR;  
v_docstatus VARCHAR;

BEGIN
    IF (INSERTING) THEN

        v_docstatus = (SELECT rc.docstatus FROM co_retencion_compra rc
                        WHERE rc.co_retencion_compra_id = :new.co_retencion_compra_id);

        IF (v_docstatus = 'CO') THEN
            v_mensaje:='Error. Documento procesado.';
            RAISE v_mensaje;
        ELSE 
            RETURN NEW;
        END IF; 

     ELSIF (DELETING) THEN

        v_docstatus = (SELECT rc.docstatus FROM co_retencion_compra rc
                       WHERE rc.co_retencion_compra_id = :old.co_retencion_compra_id);

		IF (v_docstatus = 'CO') THEN

			v_mensaje:='Error. Documento procesado.';
			RAISE v_mensaje;

        ELSE 
                RETURN OLD;
		END IF; 

    ELSIF (UPDATING) THEN

        v_docstatus = (SELECT rc.docstatus FROM co_retencion_compra rc
                       WHERE rc.co_retencion_compra_id = :old.co_retencion_compra_id);

		IF (v_docstatus = 'CO') THEN
			v_mensaje:='Error. Documento procesado.';
			RAISE v_mensaje;
		END IF;

        ELSE 
            RETURN NEW;
	END IF;

END CO_RETENCION_COMPRA_LIN_TRG
