trigger.body:
v_processed VARCHAR;

BEGIN

IF DELETING THEN 
    v_processed := (select docstatus from co_retencion_venta where co_retencion_venta_id = :old.co_retencion_venta_id);

    IF v_processed <> 'BR' THEN
        RAISE NO_DATA_FOUND;
    ELSE
        RETURN OLD; 
    END IF;
ELSE 
    RETURN NEW; 
END IF; 

END CO_RETENCION_VENTA_TRG
