trigger.body:
v_base_imp NUMBER;
v_subtotal NUMBER;
v_valor_impuesto NUMBER;

v_Message VARCHAR;

BEGIN

    IF DELETING THEN 
        RETURN OLD; 
    ELSE 
        if (:new.tipo = 'FUENTE') then

            select sum(base_imp_retencion) 
              into v_base_imp
              from co_retencion_compra_linea 
             where tipo = 'FUENTE'
               and co_retencion_compra_id = :new.co_retencion_compra_id
               and co_retencion_compra_linea_id <> :new.co_retencion_compra_linea_id;

            v_base_imp := COALESCE(v_base_imp,0) + :new.base_imp_retencion;

            select i.totallines
              into v_subtotal
              from c_invoice i
             inner join co_retencion_compra rc on i.c_invoice_id = rc.c_invoice_id
             where rc.co_retencion_compra_id = :new.co_retencion_compra_id;

            if(v_subtotal < v_base_imp  ) then
                v_Message:='La base imponible de las retenciones tipo RENTA no puede ser mayor al valor de la base imponible de la factura';
                RAISE v_Message;
            end if;
        else
            select sum(base_imp_retencion) 
              into v_base_imp
              from co_retencion_compra_linea 
             where tipo = 'IVA'
               and co_retencion_compra_id = :new.co_retencion_compra_id
               and co_retencion_compra_linea_id <> :new.co_retencion_compra_linea_id;

            v_base_imp := COALESCE(v_base_imp,0) + :new.base_imp_retencion;

            /*
            * Cambio Realizado el 2014-08-12
            * Consulta original
              select abs(grandtotal - i.totallines)
              into v_valor_impuesto
              from c_invoice i
              inner join co_retencion_compra rc on i.c_invoice_id = rc.c_invoice_id
              where rc.co_retencion_compra_id = :new.co_retencion_compra_id;
            */

            select sum(taxamt)
              into v_valor_impuesto
              from c_invoicetax i
             inner join co_retencion_compra rc on i.c_invoice_id = rc.c_invoice_id
             where rc.co_retencion_compra_id = :new.co_retencion_compra_id;

            if(v_valor_impuesto < v_base_imp) then
                v_Message:='La base imponible de las retenciones tipo IVA no puede ser mayor al valor del impuesto de la factura';
                RAISE v_Message;
            end if;

        end if;


        RETURN NEW; 
    END IF; 

END CO_VALIDA_RET_COMPRA_TRG
