function.body:
v_Resultstr VARCHAR:=''; 
 v_formaPago VARCHAR:='';
BEGIN 

select f.em_ats_codigo
into v_formaPago
from c_invoice c, fin_paymentmethod f
where c.fin_paymentmethod_id = f.fin_paymentmethod_id
  and c.c_invoice_id = p_invoice_id;

        select xmlelement(name "formasDePago" ,
                       xmlelement(name "formaPago", v_formaPago)
                       ) into v_Resultstr;

    RETURN v_Resultstr;
END ATS_FORMAS_PAGO
