function.body:
v_total_comision NUMBER:=0; BEGIN
select COALESCE(sum(cd.valor),0) into v_total_comision from no_re_comision rc, no_re_comision_detalle cd, c_period p where rc.c_period_id = p.c_period_id and rc.no_re_comision_id = cd.no_re_comision_id and cd.c_bpartner_id = p_empleado_id and rc.name NOT IN ('BONO') and p.c_period_id =p_period_id;
return v_total_comision; END NO_RE_COMISION
