trigger.body:
v_ResultStr VARCHAR(2000):='';
v_mensaje VARCHAR;
v_qry VARCHAR;
V_DOCUMENT VARCHAR (30);

BEGIN
	IF(UPDATING) THEN

	IF EXISTS (SELECT * FROM no_rol_provision_line_mes WHERE DOCUMENTNO =  :NEW.DOCUMENTNO ) THEN 

	 UPDATE no_rol_provision_line_mes SET pagado = 'Y' WHERE DOCUMENTNO = :NEW.DOCUMENTNO;		

	END IF ;
	END IF ;



END NO_ACTUALIZA_PROVMES_TRG
