trigger.body:

BEGIN


	IF INSERTING THEN
		:NEW.descuento_iess = :NEW.sueldo * 0.0945;
		:NEW.total_recibir = :NEW.sueldo - :NEW.descuento_iess - :NEW.VALOR_PRESTAMO;		
	RETURN NEW;	


	ELSIF UPDATING THEN	 
		:NEW.descuento_iess = :NEW.sueldo * 0.0945;		
		:NEW.total_recibir = :NEW.sueldo - :NEW.descuento_iess - :NEW.VALOR_PRESTAMO;		
	RETURN NEW;
	END IF;

END NO_CALCULA_TOTAL_IESS
