trigger.body:
fecha_inicio_cont DATE;



BEGIN

    IF UPDATING OR INSERTING THEN

        UPDATE c_bpartner
        SET em_no_fechaingreso = :new.fecha_inicio
        WHERE c_bpartner_id = :new.c_bpartner_id;

        UPDATE c_bpartner
        SET em_no_fechasalida = :new.fecha_fin
        WHERE c_bpartner_id = :new.c_bpartner_id;

    END IF;
    RETURN NEW;
END NO_CONTRATO_EMP_DATE_TRG
