trigger.body:
v_mensaje VARCHAR;
v_value VARCHAR;

BEGIN
    IF (INSERTING) THEN
            :NEW.VALUE = :NEW.taxid;
            RETURN  NEW; 

    ELSIF (UPDATING) THEN
          IF :NEW.taxid <> :OLD.taxid then
               :NEW.VALUE = :NEW.taxid;
          END IF;

          RETURN  NEW;

    ELSE RETURN NEW;

	END IF;
END NO_GENERA_VALUE_TRG
