trigger.body:
	v_cedula character varying(32);
BEGIN

	IF INSERTING THEN 

	IF (SELECT COUNT (*)> 0 FROM no_novedad_linea WHERE NO_NOVEDAD_ID = :new.NO_NOVEDAD_ID AND C_BPARTNER_ID = :new.C_BPARTNER_ID) THEN 

	raise exception '%', '@NO_UNICO_EMP_NOVEDAD@';
	ELSE
	return new; 

	END IF;

	END IF;



END NO_UNICO_LINEA_EMPLEADO_TRG
