trigger.body:
days NUMBER;
v_mensaje VARCHAR;


BEGIN
    days := DATE_PART('day', :new.em_ne_fecha_fin::timestamp - :new.em_ne_fecha_inicio::timestamp);

    IF (UPDATING OR INSERTING) THEN	


        :new.em_ne_total_dias=to_number(days);

    END IF;
    RETURN NEW; 
END NO_VACACIONES_SALDO2_TRG
