trigger.body:
    v_estado varchar(60);

BEGIN
    IF DELETING THEN

            select estado
            into v_estado
            from no_pago_line where no_pago_line_id=:old.no_pago_line_id;
            IF(v_estado <>'BR')THEN
              --  RAISE NO_DATA_FOUND;
                RAISE NO_DATA_FOUND;
            END IF;
            RETURN OLD;
        ELSE
            return new;
    END IF;

END NO_VALIDA_DROP_PAGOLINE_TRG
